from typing import List
# https://leetcode.com/problems/word-break/

# discuss 참고 할수 밖에 없었다.
class Solution:

    def wordBreak(self, s: str, wordDict: List[str]) -> bool:
        
        # 문자열의 처음위치와 끝나는 위치를 저장하기 위한 배열
        dp = [True]+[False]*len(s) # s False로 채워진 s보다 하나 긴 배열 선언

        for i in range(0, len(s)):
            for word in wordDict:
                # 문자열의 시작(dp 배열로 판단)이 True and 해당 문자열이 사전에 포함되는 경우 dp[i+1] True로 
                # index 너무 헷갈린다.
                if dp[i-(len(word)-1)] and s[i-(len(word)-1):i+1]==word:
                    dp[i+1] = True # dp는 s보다 한개 더 기니깐.

        return dp[-1] # 맨 마지막꺼 보면 판단가능

